package assembly.stone.itassembly.setting.adapter;import android.annotation.SuppressLint;import android.content.Context;import android.content.res.Resources;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.BaseAdapter;import android.widget.TextView;import assembly.stone.itassembly.R;public class SettingBottomAdaper extends BaseAdapter {	private LayoutInflater inflater;	public static final Integer[] SETTINGTOP_NAME = { R.string.version_update, R.string.about_us, R.string.fb_tx,			R.string.share_tx, R.string.personal_customization };	private Resources mResources;	public boolean isVisible = false;	public SettingBottomAdaper(Context mContext) {		inflater = (LayoutInflater) mContext.getSystemService(Context.LAYOUT_INFLATER_SERVICE);		mResources = mContext.getResources();	}	@Override	public int getCount() {		return SETTINGTOP_NAME.length;	}	public void setVersionVISIBLE(boolean isVisible) {		this.isVisible = isVisible;		notifyDataSetChanged();	}	@Override	public Object getItem(int position) {		return SETTINGTOP_NAME[position];	}	@Override	public long getItemId(int position) {		return position;	}	@SuppressLint("InflateParams")	@Override	public View getView(final int position, View convertView, ViewGroup parent) {		ViewHolder holder = null;		if (convertView == null) {			holder = new ViewHolder();			convertView = inflater.inflate(R.layout.setting_bottom_item, null);			holder.setting_bottom_text = (TextView) convertView.findViewById(R.id.setting_bottom_text);			holder.new_version = (TextView) convertView.findViewById(R.id.new_version);			convertView.setTag(holder);		} else {			holder = (ViewHolder) convertView.getTag();		}		holder.setting_bottom_text.setText(mResources.getString(SETTINGTOP_NAME[position]));		if (position == 0) {			holder.new_version.setVisibility(View.VISIBLE);		} else {			holder.new_version.setVisibility(View.GONE);		}		return convertView;	}	private class ViewHolder {		private TextView setting_bottom_text;		private TextView new_version;	}}